mapper.update:
UPDATE

    V
SET
    V.VisitFromDateTime = #{visitFromDateTime}
    , V.VisitToDateTime = #{visitToDateTime}
FROM
    VisitorHistory AS V
WHERE
    V.VisitorHistorySeq = #{visitorHistorySeq}

UPDATE

    V
SET
    V.VisitApprovalYN = #{visitApprovalYN}
    , V.Approval = 'Y'
    , V.VisitApprovalDateTime = #{visitApprovalDateTime}
    , V.VisitApprovalComment = #{visitApprovalComment}

FROM
    VisitorHistory AS V
WHERE
    VisitorHistorySeq = #{visitorHistorySeq}

UPDATE

    V
SET





    V.RejectFlag = 'Y'
    , V.RejectType = #{rejectType}

        , V.RejectComment = #{rejectComment}


FROM
    VisitorHistory AS V
WHERE
    V.VisitorHistorySeq = #{visitorHistorySeq}

mapper.select:
SELECT count(*) FROM visitorhistory V WHERE VisitorID = #{visitorID}
AND CONVERT(varchar(50), V.PlanFromDateTime, 23) >= CONVERT(#{planFromDateTime}, 23) AND CONVERT(varchar(50), V.PlanToDateTime, 23) <= CONVERT(#{planToDateTime}, 23)
AND V.DeleteFlag = 'N'

mapper.insert:
INSERT INTO visitorhistory (

