    , CarryStuffUsed
    , CarryStuffPurpose
    , DeleteFlag
    , VisitPurpose


    ) VALUES (

    #{visitorID}
    , #{visitorReservationNumber}
    , #{visitorName}
    , #{visitorBirth}
    , #{visitorMobile}
    , #{visitorCompany}
    , #{visitorPosition1}
    , #{visitorPosition2}
    , #{visitorPosition3}
    , 'N'
