    , 'N'
    , #{visitApprovalDateTime}
    , #{eduCompleteDateTime}
    , #{carryStuff}
    , #{carryStuffSerialNo}
    , #{carryStuffUsed}
    , #{carryStuffPurpose}
    , 'N'
    , #{visitPurpose}


)

mapper.update:
UPDATE
    Visitorhistory
SET
    VisitorReservationNumber = #{visitorReservationNumber}
WHERE
    VisitorHistorySeq = #{visitorHistorySeq}
